            ComBoost = icon.Com ?? 0;
            MagBoost = icon.Mag ?? 0;
            SubBoost = icon.Sub ?? 0;
            GroupTravelBoost = icon.Tra ?? 0;
            HealBoost = icon.Heal ?? 0;
            ChaBoost = icon.Cha ?? 0;
            LogBoost = icon.Log ?? 0;
            TacBoost = icon.Tac ?? 0;
            TeachBoost = icon.Teach ?? 0;
            CounterBoost = icon.Counter ?? 0;
            LeadBoost = icon.Lead ?? 0;
